*,
*::after,
*::before {
    margin: 0;
    padding: 0;
    /*change the box model so they are not added to the
    total width/height that we specify for a box */
    box-sizing: inherit;
}

html {
    /* simplify the rem units, making it from 16 px to 10px but
    but not hard setting it to the browser so users can zoom
    in */
        font-size: 62.5%;
        box-sizing: border-box;
           
        @media only screen and (max-width: $bp-medium){
         font-size: 50%;
        }

       
    }


body {
    font-family: 'Montserrat', sans-serif;
    
}

code {
    border-radius: 2%;
}

a {
    text-decoration: none;
}

