sources.sass\\layout\\_backsidebar.scss:
.back__sidebar {
    border-right: 1px solid $orange;
    display: grid;
    height: 100vh;
    align-content: center;
    &__li--item {
        list-style: none;
        transition: all 0.2s;
        position: relative;
        display: block;
        width: 100%;
        padding: 1rem;
        text-align: left;

        &:hover {
           > a  {
                transition: all 1s;
                color: $purple;
            }
        }
        &:not(:last-child) {
            margin-bottom: .5rem;
        }
       
        &::before {
            content: "";
            position: absolute;
            top: 0;
            left: 0;
            height: 100%;
            width: 3px;
            background-color: $orange;
            transform: scaleY(0);
            transition: transform .2s,
                        width .4s cubic-bezier(1,0,0,1) .2s ;
        }

    
        &:hover::before {
            transform: scaleY(1);
            width: 100%;
            
        }
     
    }
    &__link {

        @media only screen and (max-width: $bp-small){
            justify-content: center;
        }
        &--icon {
            width: 2rem;
            height: 2rem;
            margin-right: 1rem;
            fill: currentColor;
            @media only screen and (max-width: $bp-small){
                margin-right: 0rem;
            }
        }

        &--text {
            @media only screen and (max-width: $bp-small){
                visibility: hidden;
                width: 0;
                height: 0;
               }
        }

    }
    &__link,
    &__link:visited, &__link:active {
    display: flex;
    align-items: center;
    color: $bonewhite;
    text-decoration: none;
    font-size: 1.4rem;
    text-transform: uppercase;
    position: relative;
    z-index: 10;
    }

    &__social {
        display: flex;
        justify-content: center;
    }

    &__icon {
        fill: currentColor;
        width: 2.5rem;
        height: 2.5rem;
        margin: 1rem;
        transition: all 0.2s;
        &:hover {
           fill: currentColor;
        }
    }

    &__navigation {
     
        text-align: center;
        display: grid;
        grid-row-gap: 1rem;
        grid-template-rows: repeat(4, min-content);
    }

  }

sources.sass\\layout\\_navigation.scss:
.navigation {
    &__checkbox {
        display: none;
    }
    &__button {
        background-image: radial-gradient(#4d3e67, $purple);
        height: 7rem;
        width: 7rem;
        position: fixed;
        top: 6rem;
        right: 6rem;
        border-radius: 50%;
        z-index: 2000;
        box-shadow: 0 2rem 5rem rgba(0,0,0,0.8);
        text-align: center;
        cursor: pointer;

    }
    &__background {
        height: 6rem;
        width: 6rem;
        border-radius: 50%;
        position: fixed;
        top: 6.5rem;
        right: 6.5rem;
        background-image: radial-gradient($purple, $purple);
        z-index: 1000;
        transition: transform .8s cubic-bezier(0.86, 0, 0.07, 1);

        //transform: scale(90);
    }

    &__nav {
    opacity: 0;
    visibility: hidden;
    width: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100vh;
    overflow: hidden;
    position: fixed;
    top: 0;
    right: 0;
    z-index: 1500;
    transition: all .8s cubic-bezier(.67,-0.78,.1,1.54);
    
    }
    &__list {
        position: absolute;
        list-style: none;
        text-align: center;
        
    }

    &__item {
     
        margin: 1rem;
        &--icon {
            width: 3rem;
            height: 3rem;
            margin-right: 2rem;
            fill: currentColor;
        }
    }

    &__link {
        display: flex;
        align-items: center;
        &:link,
        &:visited {

            font-size: 3rem;
            font-weight: 300;
            padding: 1rem 2rem;
            color: $orange;
            text-decoration: none;
            text-transform: uppercase;
            background-image: linear-gradient(120deg, transparent 0%, transparent 50%, $orange 50%);
            background-size: 225%;
            transition: all .8s;
            
           
        }

        &:hover,
        &:active {
            background-position: 100%;
            color: $purple;
            transform: translateX(1rem);
            

        }
        
    }

    //functionality
    &__checkbox:checked ~ &__background {
        transform: scale(80);
        
    }

    &__checkbox:checked ~ &__nav {
        height: 100vh;
        opacity: 1;
        width: 100%;
        visibility: visible;
        
    }

    &__checkbox:checked + &__button &__icon {
       background-color: transparent;
    }

    &__checkbox:checked + &__button &__icon::before {
        transform: rotate(135deg);
        top: 0;
     }

     &__checkbox:checked + &__button &__icon::after {
         top: 0;
        transform: rotate(-135deg);
     }

    //icon 
    &__icon {
        position: relative;
        margin-top: 3.3rem;
        &,
        &::before,
        &::after {
            width: 3rem;
            height: 2px;
            background-color: $orange;
            display: inline-block;
        }

        &::before,
        &::after { 
            content: "";
            position: absolute;
            left: 0;
            transition: all .2s;

        }
        &::before {
            top: -.8rem;
        }
        &::after {
            top: .8rem;
        }
    }

    &__button:hover &__icon::before {
        top: -1rem
    }

    &__button:hover &__icon::after {
        top: 1rem
    }


}

sources.sass\\pages\\_index.scss:
.index {
  height: 100vh;
  width: 100%;
  perspective: 150rem;
  background-color: orangered;
  position: relative;
  overflow: hidden;

  &__side {
    position: absolute;
    left: 0;
    top: 0;
    height: 100%;
    width: 100%;
    backface-visibility: hidden;
    transition: all 1.5s ease;

  }

  &__front {
  background-color: $orange;
  display: grid;
  align-content: center;
  justify-content: center;
  justify-items: center;
  grid-row-gap: 5rem;
  z-index: 10;

  &--logo {
    width: 60%;
  }

  &--header {
      color: #0b0b28;
      text-transform: uppercase;
      letter-spacing: 10px;
      font-weight: 200;
      padding: 0 2rem;
      @media only screen and (max-width: $bp-medium){
        letter-spacing: 2px;
        font-size: 2rem;
       }
       @media only screen and (max-width: $bp-small){
        letter-spacing: 1px;
        font-size: 1.5rem;
       }
       @media only screen and (max-width: $bp-extrasmall){
        letter-spacing: 1px;
        font-size: .8rem;
       }
  }
  }

  &__back {
    background-image: linear-gradient(to left bottom, #4d3e67, $purple);
    transform: rotateY(180deg);
    display: grid;
    grid-template-columns: 20% 80%;

    


  

    &--header {
      padding: 1rem;
      margin: 1rem;
      font-size: 2rem;
      color: $bonewhite;
      font-weight: 700;
      span {
        color: orange;
        font-size: 2rem;
      }
    }
    &--subheader {
      padding: 1rem;
      margin: 1rem;
      color: $bonewhite;
    }

    &--grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(30rem, 1fr));

      //grid-auto-rows: min-content;
    }

    &--sidecolumn {
      display: grid;
      grid-template-columns: 1fr 1fr;
      height: 100vh;
      align-content: center;

      @media only screen and (max-width: $bp-small){
        justify-content: center;
        height: 100%;
      }
    }

    &--row {
      display: grid;
      justify-content: center;
      align-items: center;
    }
  
  }

  // &:hover &__front {
  //   transform: rotateY(180deg);
  // }

  // &:hover &__back {
  //   transform: rotateY(0deg);
  // }

  }

